<script src="https://cdn.bootcss.com/jquery/3.2.1/jquery.js"></script>
<script>
    var num = 1;
    var mss = [
        '我的前半生',
        '罗子君贺函唐晶',
        '老看电视剧浪费时间',
        '李梓睿有时候不乖',
        '我什么时候才能瘦啊？',
        '减肥好难？',
        '工资太少怎么办',
        '怎么样能多赚钱',
        '我厉不厉害',
        '李梓睿乖乖听话啊',
        '李梓睿妈妈爱你啊',
        '李梓睿健康快乐成长',
        '我要变得越来越瘦越来越漂亮越来越能赚钱',
        '学习很重要的',

        '只有不断学习的人，才不会被淘汰！',
    ];

    setInterval(function(){
        var mssNum = parseInt((mss.length)*Math.random());
        console.log(num,num%2,mssNum);
        if(num % 2 == 1){
            $("#img").animate({
                'margin-left':100,'margin-top':0, opacity: 'show'
            }, 4000,'swing',function(){
                $("#img").animate({
                    'margin-left':100,'margin-top':170, opacity: 'show'
                },1000);
            });
            showMsg(mss[mssNum]);
        }else{
            $("#img").animate({
                'margin-left':600,'margin-top':0, opacity: 'show'
            },4000,'swing',function(){
                $("#img").animate({
                    'margin-left':600,'margin-top':170, opacity: 'show'
                }, 1000);
            });
            showMsg(mss[mssNum]);
        }

        num++;
    },3000);

    function showMsg(msg){
        $('p').text(msg);
    }
    $("#img").click(function(){
        showMsg('非礼啊,别动手动脚的');
    });
</script>
